import React, {Component} from "react"
import {Link} from "react-router-dom"

import axios from "axios"

import TshirtTable from "./TshirtTable"
import Logout from "./Logout"

import {ACCESS_LEVEL_GUEST, ACCESS_LEVEL_ADMIN, SERVER_HOST} from "../config/global_constants"


export default class DisplayAllTshirts extends Component
{
    constructor(props) 
    {
        super(props)

        this.state = {
            tshirts:[]
        }
    }


    componentDidMount() 
    {
         axios.get(`${SERVER_HOST}/tshirts`)
        .then(res =>
        {
            if(res.data)
            {
                if (res.data.errorMessage)
                {
                    console.log(res.data.errorMessage)
                }
                else
                {
                    console.log("Records read")
                    this.setState({tshirts: res.data})
                    console.log(res)
                }
            }
            else
            {
                console.log("Record not found")
            }
        })
    }


    render() 
    {   
        return (           
            <div className="form-container">
                {sessionStorage.accessLevel > ACCESS_LEVEL_GUEST ? 
                    <div className="logout">
                        <Logout/>
                    </div>
                :
                    <div>
                        <Link className="green-button" to={"/Login"}>Login</Link>
                        <Link className="blue-button" to={"/Register"}>Register</Link>  
                        <Link className="red-button" to={"/ResetDatabase"}>Reset Database</Link>  <br/><br/><br/></div>
                }

                <div className="table-container">
                    <TshirtTable tshirts={this.state.tshirts} />

                    {sessionStorage.accessLevel >= ACCESS_LEVEL_ADMIN ?
                        <div className="add-new-car">
                            <Link className="blue-button" to={"/AddCar"}>Add New Car</Link>
                        </div>
                    :
                        null
                    }
                </div>
            </div> 
        )
    }
}
