import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import TshirtTable from "./TshirtTable";
import Logout from "./Logout";
import { ACCESS_LEVEL_GUEST, ACCESS_LEVEL_ADMIN, SERVER_HOST } from "../config/global_constants";
import SortTshirts from "./SortTshirts";
import FilterTshirts from './FilterTshirts';
import SearchTshirts from "./SearchTshirts";

export default class DisplayAllTshirts extends Component {
    constructor(props) {
        super(props);

        this.state = {
            tshirts: [],
            originalTshirts: [],
            filters: {
                size: [],
                color: [],
                style: []
            },
            searchQuery: '',
            sortOption: ''
        };
    }

    componentDidMount() {
        axios.get(`${SERVER_HOST}/tshirts`)
            .then(res => {
                if (res.data) {
                    console.log("Records read");
                    this.setState({ originalTshirts: res.data, tshirts: res.data });
                } else {
                    console.log("Record not found");
                }
            });
    }

    handleSortChange = (sortOption) => {
        this.setState({ sortOption }, this.applyFiltersAndSort);
    }

    handleFilterChange = (filters) => {
        this.setState({ filters }, this.applyFiltersAndSort);
    }

    handleSearch = (searchQuery) => {
        this.setState({ searchQuery }, this.applyFiltersAndSort);
    }

    applyFiltersAndSort = () => {
        let { originalTshirts, filters, sortOption, searchQuery } = this.state;

        // Застосування пошуку
        let result = originalTshirts.filter(tshirt =>
            (!searchQuery || tshirt.brand.toLowerCase().includes(searchQuery.toLowerCase()) ||
                tshirt.color.toLowerCase().includes(searchQuery.toLowerCase()) ||
                tshirt.country_of_manufacture.toLowerCase().includes(searchQuery.toLowerCase())) &&
            (!filters.size.length || filters.size.includes(tshirt.size)) &&
            (!filters.color.length || filters.color.some(color => tshirt.color.toLowerCase().includes(color.toLowerCase()))) &&
            (!filters.style.length || filters.style.some(style => tshirt.style.toLowerCase().includes(style.toLowerCase())))
        );

        // Застосування сортування
        if (sortOption === 'price-low-high') {
            result.sort((a, b) => a.price - b.price);
        } else if (sortOption === 'price-high-low') {
            result.sort((a, b) => b.price - a.price);
        } else if (sortOption === 'brand') {
            result.sort((a, b) => a.brand.toLowerCase().localeCompare(b.brand.toLowerCase()));
        }

        this.setState({ tshirts: result });
    }

    render() {
        return (
            <div className="form-container">
                {/* Authentication and navigation links */}
                <SearchTshirts onSearch={this.handleSearch} />
                <SortTshirts onSortChange={this.handleSortChange} />
                <FilterTshirts onFilterChange={this.handleFilterChange} />

                <div className="table-container">
                    <TshirtTable tshirts={this.state.tshirts} />
                    {/* Conditional rendering for admin access */}
                </div>
            </div>
        );
    }
}
