import React, {Component} from "react"

export default class SortTshirts extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showOptions: false
        };
    }

    toggleOptions = () => {
        this.setState(prevState => ({ showOptions: !prevState.showOptions }));
    }

    handleSort = (sortOption) => {
        console.log(`Sorting by ${sortOption}`);
        this.props.onSortChange(sortOption);
        this.toggleOptions();
    }

    render() {
        return (
            <div>
                <button onClick={this.toggleOptions}>Sort</button>
                {this.state.showOptions ? (
                    <div className="sort-options">
                        <ul>
                            <li onClick={() => this.handleSort('price-low-high')}>Price: Low to High</li>
                            <li onClick={() => this.handleSort('price-high-low')}>Price: High to Low</li>
                            <li onClick={() => this.handleSort('style')}>Style</li>
                        </ul>
                    </div>
                ) : null}
            </div>
        );
    }
}
